import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import Navigation from './Navigation';
import axios from 'axios';
const {Order} = require('..backend/models/orders')

const express = require("express");
const router = express.Router();


const UpdateOrder = (props) => {

    const [orderToUpdate, setOrderToUpdate] = useState({});

    Order.findById(orderId)
    .exec((err,order) => {
        if(err || !order) {
            return res.status(400).json({
                error: errorHandler(err)
            });
        }
        // We make the order available on the request object
        req.order = order
        const {name, lastName, country, city, service, price,  date} = order


      //  next() // Once you find the orderById, execute the callback
    })    

const orderId = props

axios.get(`http://localhost:3001/order/${orderId}`)
.then(data =>  
    { if(data.error) {
        setOrderToUpdate({...orderToUpdate, error:true, success:false})
        console.log(data.error)
    } 

        setOrderToUpdate({...orderToUpdate, data})
    })


const clickSubmit = (event) =>
{
    event.preventDefault()
       console.log(event.target.value)

   
   
       console.log(name)
       console.log(lastName)
   const isValid = Boolean(order.name && order.lastName && order.country && order.city && order.service)

   if (isValid) {

       axios.put('http://localhost:3001/updateOrder/:orderId', order)
        .then(data =>   
      {
           if(data.error) {
               setOrder({...order, error: true, success:false})
               console.log(data.error)
           }
           
       
        })
        setOrder({
           ...order,
           name:'', 
           lastName:'',
           country:'',
           city:'',
           service: '',
           price:'',
           note: '',
           date:'',
           success:true,
   });
           setSuccessMessage('Nueva Orden creada correctamente')
           setErrorMessage('')
}
       else {
           setErrorMessage('Se deben completar todos los campos')
           setSuccessMessage('')
       
       

       }
        };


return (
   <form   onSubmit={clickSubmit}>

   <div className='pl-2 row mt-4'>
       
      <div class="form-group col-md-4">
          <label>Name <input className='form-control'  type="text"
           name="name"
           value= {order.name}
           onChange={handleChange}
           placeholder="First Name"
           />
           </label>
          </div>
          <div class="form-group col-md-4">
          <label>Last Name<input className='form-control'  type="text"
           name="lastName"
           value= {order.lastName}
           onChange={handleChange}
           placeholder="Last Name"
           />
           </label>
          </div>

      <div className='form-group col-md-4'>
           <label>Date
           <input className='form-control' type="date"
           name="date"
           value= {order.date}
           onChange={handleChange}
           />
           </label>
           </div>

           <div className='row mt-4'>
           <div className='form-group col-md-4'>
           <label>Choose a Service: </label>

           <select  value= {order.service} onChange={handleChange} name ='service' id='cars'>
              <option value ="chain">Repair Chain</option>
              <option value ="service">Complete Service</option>
              <option value ="brakes">Fix Brakes</option>
              <option value ="puncture">Puncture</option>
              <option value ="rays">Fix Rays</option>
              <option value ="rubber">Rubber Rides</option>
             
           </select>
      
           </div>

           <div className='form-group col-md-4'>
           <label>Country
           <input className='form-control' type="text"
           name="country"
           value= {order.country}
           onChange={handleChange}
           />
           </label>
          </div>

           <div className='form-group col-md-4'>
           <label>City
           <input className='form-control' type="text"
           name="city"
           value= {order.city}
           onChange={handleChange}
           />
           </label>
           </div>

           <div className='row mt-4'>
           <div className='form-group col-md-4'>
           <label>Price
           <input className='form-control' type="number"
           name="price"
           value = {order.price}
           onChange= {handleChange}
          />
          </label>
          </div>
          <div className='form-group col-md-8'>
          <label>Note

          
           <textarea className='form-control' rows='2' cols='250'
           name="note"
           value = {order.note}
           onChange= {handleChange}
           />
          </label>
          </div>
           
          <div className='col-4'>
           <button  type= "submit" className='btn btn-lg btn-success mt-5'>Success</button>
          </div>
           
              </div>
           </div>
       </div>
   
      

   </form>
)
}
