import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';


const Customer = () => {

    const [data, setData] = useState({
        name:'',lastName:'', email:'', phoneNumber: '', country:'', city:'', errror: '', success:false })

    const handleChange = (e) => {
        
        const {name ,value} = e.target
        setData({...data, [name] : value})


    }

    const clickSubmit = (event) => {
            //
        
            event.preventDefault()
            // setError("")
            const {name, lastName, email, phoneNumber, country, city, error, success} = data 
            const customer = {
                name, lastName, email, phoneNumber, country,city
            }

            setData({...data, error: false})

            axios.post('http://localhost:3001/newCustomer', customer)
            .then(() =>  response => {
                if(response.error) {
                    setData({...data, error: response.error, success:false})
                    console.log(response.error)
                }
                else {
                    setData({
                        ...data,
                        name:'', 
                        lastName:'',
                        email:'',
                        phoneNumber: '',
                        country:'',
                        city:'',
                        errror: '',
                        success:true
                })
            }             //console.log('Customer created'))
            
            }); 
            };
            // init()
          
            // window.location.reload()
        
    

    return (
        <div className='container mt-5'>
      
        
    <h2>Add New Customer</h2>
    <form  onSubmit={clickSubmit} className='row g-3 mt-3'>
        <div className='col-md-6'>
            <label htmlFor="firstName" className="form-label">First Name</label>
            <input type="text" onChange={handleChange} name="name" value={data.name}  className='form-control' id="firstName" required></input>
        </div>
        <div className='col-md-6'>
            <label htmlFor="lastName" className="form-label">Last Name</label>
            <input type="text" onChange={handleChange} name='lastName' value={data.lastName} className='form-control' id="lasttName" required></input>
        </div>

    <div className='col-md-8'>
        <label htmlFor="emailInfo" className='form-label'>E-mail</label>
        <input type="email" onChange={handleChange}  name='email' value={data.email} className='form-control' id="emailInfo" required></input>
    </div>

    <div className='col-md-4'>
        <label htmlFor="phoneNumber" className='form-label'>Phone Number</label>
        <input type="text" onChange={handleChange}  name='phoneNumber' value={data.phoneNumber} className='form-control' id="phoneNumber" required placeholder='+598'></input>
    </div>

    <div className='col-md-4'>
            <label htmlFor="country" className="form-label">Country</label>
            <input type="text" onChange={handleChange}  name='country' value={data.country} className='form-control' id="country" required></input>
        </div>

        <div className='col-md-4'>
            <label htmlFor="city" className="form-label">City</label>
            <input type="text" onChange={handleChange}  name='city' value={data.city} className='form-control' id="city" required></input>
        </div>

        <div className='col-md-12'>
            <button type='submit' className='btn btn-primary'>Submit</button>
        </div>
    </form>
    

    </div>

    )
}


export default Customer;
