import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';
import {Link} from "react-router-dom"



const SearchCustomer = () => {

    const [search, setSearch] = useState({name:"", lastName:""});

    const handleChange = (e) => {
        
        const {name ,value} = e.target
        console.log(value)
        setSearch({...search, [name] : value})

    }

    return (
        <div className='input-group-text search'>
            <div class="ml-3 input-group-prepend">
                <span className='input-group-text' id='name'>Customer Name</span>
              
            </div>

        <input type="search" onChange={handleChange} name = "name" value={search.name} className='form-control' placeholder='Customer Name' aria-label='customer name' aria-describedby='name'></input>
    
      
           
            

        <span className='input-group-text' id='lastName'>Customer Last Name</span>
        <input type="search" onChange={handleChange} name = "lastName" value={search.lastName} className='form-control' placeholder='Customer Last Name' aria-label='customer last name' aria-describedby='lastName'></input>

        </div> 
    )
}


export default SearchCustomer;
