import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';
import {Link} from "react-router-dom"

const Customer = () => {

    const [values, setValues] = useState({
        name:'',lastName:'', email:'', phoneNumber: '', country:'', city:'', error: '', success:false })

    const handleChange = (e) => {
        
        const {name ,value} = e.target
        setValues({...values, [name] : value})


    }

    const {name, lastName, email, phoneNumber, country, city, error, success} = values

    const clickSubmit = (event) => {
            //
        //  console.log(values)
            event.preventDefault()
            // setError("")
      
            const customer = {
                name, lastName, email, phoneNumber, country,city,
            }

            setValues({...values, error: false})
            console.log(values)

            axios.post('http://localhost:3001/newCustomer', customer)
             .then(function(data)    
           {
                if(data.error) {
                    setValues({...values, error: data.error, success:false})
                    console.log(data.error)
                }
                else {
                
                    setValues({
                        ...values,
                        name:'', 
                        lastName:'',
                        email:'',
                        phoneNumber: '',
                        country:'',
                        city:'',
                        error: '',
                        success:true
                })
            }             //console.log('Customer created'))
            
             }); 
             };
            // init()
          
            // window.location.reload()
        
     // If success is true then this div will be showed to the user
     const showSuccess = () => (
        <div className = "alert alert-info" style = {{display: success ? "": 'none'}}>
            New Customer is created. Return to Home Page <Link to="/"></Link>
        </div>
    )

    return (
        <div className='container mt-5'>
      
        
    <h2>Add New Customer</h2>
    <form className='row g-3 mt-3'>
        <div className='form-group col-md-6'>
            <label htmlFor="firstName" className="form-label">First Name</label>
            <input type="text" onChange={handleChange} name="name" value={values.name}  className='form-control' id="firstName" required></input>
        </div>
        <div className='form-group col-md-6'>
            <label htmlFor="lastName" className="form-label">Last Name</label>
            <input type="text" onChange={handleChange} name='lastName' value={values.lastName} className='form-control' id="lasttName" required></input>
        </div>

    <div className='form-group col-md-8'>
        <label htmlFor="emailInfo" className='form-label'>E-mail</label>
        <input type="email" onChange={handleChange}  name='email' value={values.email} className='form-control' id="emailInfo" required></input>
    </div>

    <div className='form-group col-md-4'>
        <label htmlFor="phoneNumber" className='form-label'>Phone Number</label>
        <input type="text" onChange={handleChange}  name='phoneNumber' value={values.phoneNumber} className='form-control' id="phoneNumber" required placeholder='+598'></input>
    </div>

    <div className='form-group col-md-4'>
            <label htmlFor="country" className="form-label">Country</label>
            <input type="text" onChange={handleChange}  name='country' value={values.country} className='form-control' id="country" required></input>
        </div>

        <div className='form-group col-md-4'>
            <label htmlFor="city" className="form-label">City</label>
            <input type="text" onChange={handleChange}  name='city' value={values.city} className='form-control' id="city" required></input>
        </div>

        <div className='col-md-12'>
            <button onSubmit={clickSubmit} type='submit' className='btn btn-primary'>Submit</button>
        </div>
    </form>
    
    {showSuccess}
    </div>

   
    
    )
}


export default Customer;
