import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';
import {Link} from "react-router-dom"



const SearchCustomer = (props) => {

    const [search, setSearch] = useState({name:"", lastName:""});

    const handleChange = (e) => {
        console.log(e.target.value)
        const {name ,value} = e.target
        console.log(value)
        console.log(name)
        setSearch({...search, [name] : value})
        console.log(search)
        props.searchTerm(search)
    }

    return (
        <div className='col-lg-6 col-md-12 col-sm-12'>
        <div className='input-group-text'>
            <div class="input-group-prepend">
                <span className='input-group-text' id='name'>Customer Name</span>
              
            </div>

        <input type="search" onChange={handleChange} name = "name" value={search.name} className='search-input' placeholder='Customer Name' aria-label='customer name' aria-describedby='name'></input>        
            
        <span className='input-group-text' id='lastName'>Customer Last Name</span>
        <input type="search" onChange={handleChange} name = "lastName" value={search.lastName} className='search-input' placeholder='Customer Last Name' aria-label='customer last name' aria-describedby='lastName'></input>

        </div>
        </div> 
    )
}


export default SearchCustomer;
