import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';
import {Link} from "react-router-dom"

const SearchProperty = (props) => {

    const [search, setSearch] = useState({firstName:"", lastName:""});

    const {firstName, lastName} = search;

    function handleChange(event) {
        const {value,name} = event.target;
        console.log(value)
        console.log(name)
        setSearch(prevValue => {
            if(name === "firstName") {
                return {
                firstName: value,
                lastName : prevValue.lastName
            };
            } else if  (name === "lastName") {
            return {
                firstName : prevValue.firstName,
                lastName : value
            }
        }
    })
}
    // const handleChange = name => event => {
    //     //console.log(e.target.value)
    //     const {name ,value} = event.target
    //     console.log(value)
    //     //const value = e.target.value
    //     // console.log(value)
    //     // console.log(name)            
    //     setSearch({...search, [name]:  event.target.value})
    //     //setSearch({...search, [name] : value})
    //     console.log(search)
    //     props.searchTerm(search)
    // }

    return (
        <div className='col-lg-6 col-md-12 col-sm-12'>
        <div className='input-group-text'>
            <div class="input-group-prepend">
                <span className='input-group-text' id='name'>Customer Name</span>
              
            </div>

        <input type="search" onChange={handleChange} name = "firstName" value={firstName} className='search-input' placeholder='Customer Name' aria-label='customer name' aria-describedby='name'></input>        
            
        <span className='input-group-text' id='lastName'>Customer Last Name</span>
        <input type="search" onChange={handleChange} name = "lastName" value={lastName} className='search-input' placeholder='Customer Last Name' aria-label='customer last name' aria-describedby='lastName'></input>

        </div>
        </div> 
    )
}


export default SearchProperty;
