import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';
import {Link, useSearchParams} from "react-router-dom"
import SearchProperty from "./Search";


const GetOrders = () => {

    const [orders, setOrders] = useState([]);
    const [filteredCustomers, setFilteredCustomers] = useState([]);
    const [q, setQ] = useState("");
    const [searchParam] = useState(["name", "lastName"]);
    var searchParam_ = String()


        // name:"",
        // email: "",
        // phone: "",
        // country: "",
        // city: ""});

    
    const ordersData = () => {
        axios.get('http://localhost:3001/history')
        .then(res => {
       //     console.log(JSON.stringify(res.data))
            setOrders(res.data)
       // setCustomers({name: res.data.name, email: res.data.email ,
            //      phone: res.data.phone, country: res.data.country, city: res.data.city});
        })
            .catch(function(error){
                console.log(error);
            })
        
    }
   

    useEffect(() => {
    ordersData()
    }, [])

const childToParent = (item) => {
console.log(item)
// const firstName = item.firstName
// const lastName = item.lastName

console.log(Object.keys(item))
const {firstName, lastName} = item
console.log(firstName)
console.log(lastName)

if (firstName != '') {
    setQ(firstName)
    
} else {
    setQ(lastName)
    
}

//const name = item.firstName
//const lastName = item.lastName
//setQ(firstName)

// const filtered = orders.filter((order) => order[searchParam_].toLowerCase().indexOf(q) > -1);

const filtered = orders.filter((order) => {

     return searchParam.some((newItem) => {
      
       return order[newItem]
             .toString()
             .toLowerCase()
             .indexOf(q.toLowerCase()) > -1
    
}
)
})

// console.log(order.lastName.toLowerCase().indexOf(q) > -1));


console.log(filtered)
setFilteredCustomers(filtered)
}

const goBack = () => (
    
    <Link to ="/">            
                <button className="btn btn-primary float-end">Home Page</button>
                </Link>           
    )

return (
    
    <div>
     <h2>Orders History</h2>

    <div className='container'>{goBack()}
            </div>
    <SearchProperty searchTerm = {childToParent}/>
    
    <table  className='col-lg-10 col-md-10 col-sm-12'>
   
    <thead>
        <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Date</th>
            <th>Service</th>
            <th>Price</th>
            <th>Country</th>
            <th>City</th>
        </tr>
    </thead>
    <tbody>

    {q ? filteredCustomers.map(customer => {
        return(
            <tr key = {customer._id}> 
                 
                 <td>{customer.name}</td>
                 <td>{customer.lastName}</td>
                 <td>{customer.date}</td>
                 <td>{customer.service}</td>
                 <td>{customer.price}</td>
                 <td>{customer.country}</td>
                 <td>{customer.city}</td>
                 </tr>
            
        )
    }) : (
    orders.map(order => {
            
            return( 
                
                <tr key = {order._id}> 
                 
                 <td>{order.name}</td>
                 <td>{order.lastName}</td>
                 <td>{order.date}</td>
                 <td>{order.service}</td>
                 <td>{order.price}</td>
                 <td>{order.country}</td>
                 <td>{order.city}</td>
                
                 </tr>
            
             
            )
    })
    )}

    </tbody>

    </table>
        
    </div>
)

}




export default GetOrders;
