import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import axios from 'axios';
import {Link} from "react-router-dom"



const SearchCustomer = () => {

    const [search, setSearch] = useState("");

    const handleChange = (e) => {
        
        const {name ,value} = e.target
        setSearch({...search, [name] : value})

    }

    return (
        <div className='input-group mb-3'>
            <div class="input-group-prepend">
                <span className='input-group-text' id='name'>Customer Name</span>
            </div>

        <input type="search" onChange={handleChange} value={search} className='form-control' placeholder='Customer Name' aria-label='customer name' aria-describedby='name'></input>
        </div> 
    )
}
