import React, {useEffect, useState} from 'react'
import Home from "./Home";
import './App.css';
import Navigation from './Navigation';
import axios from 'axios';
import Moment from 'moment';
import {Link} from "react-router-dom"

const AddPurchase = () => {

    const [order, setOrder] = useState({name: "",
     lastName:"",
     country:"", city:"" , service:"", 
     price: "", note: "",  date: "", error: false, success: false})

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [services, setServices] = useState('');
    const [userNoRegistered, setUserNoRegistered] = useState('');

    // getCategories().then(data => {
    //     if(data.error) {
    //         console.log(data.error)
    //         setError(data.error)
    //     } else {
    //         setCategories(data);
    //     }
    // }

    const getServices = () => {

        axios.get('http://localhost:3001/services')
        .then(data =>  setServices(data.data))
        .catch(function(error) { 
        console.log(error)
        }
        )
    }

    useEffect(() => {
     
        getServices();
        //loadFilteredResults(skip, limit, myFilters.filters)
        
        
       } , []);


     const handleChange = (event) =>
     {
         
         const   {name,value} = event.target
     //    console.log(name)
   //      {name = date ? value = Moment(date).format('YYYY-mm-dd') : value}
     //    console.log(value)

        setOrder({...order, [name] : value})
     }
     const {name, lastName, email, city, service, price, error,  date, note ,success} = order
  
   

     const clickSubmit = (event) =>
     {
         event.preventDefault()
            console.log(event.target.value)

        
        
            // console.log(name)
            // console.log(lastName)
            // console.log(email)

        
        const isValid = Boolean(order.name && order.lastName && order.email && order.city && order.service)

        if (isValid) {

            axios.post('http://localhost:3001/createOrder',  order)

            .then(response => {
                setOrder({
                    ...order,
                    name:'', 
                    lastName:'',
                    email:'',
                    city:'',
                    service: '',
                    price:'',
                    note: '',
                    date:'',
                    success:true,
            });
            setErrorMessage('')
            setUserNoRegistered('')
            })
             .catch(function(error) {   
               console.log(error)
                if(error.response) {
                    setOrder({...order, error: true, success:false})
                    console.log(error.response.data)
                    setSuccessMessage('')
                    setUserNoRegistered('Se debe dar de alta el usuario')
                }
                
            
             })
             
            
        console.log('hola hola hola')
             //   setSuccessMessage('Nueva Orden creada correctamente')
                
    
        }
        
            else {
                setErrorMessage('Se deben completar todos los campos')
                setSuccessMessage('')
            
            

            }
             };

        
           

        const showError = () => (

           

                <div className='alert alert-danger' style= {{display : errorMessage  ? '': 'none'}}>
                {errorMessage}
                </div>
                
            );

            const showNoUser = () => (

           

                <div className='alert alert-danger' style= {{display : userNoRegistered  ? '': 'none'}}>
                {userNoRegistered}
                <Link to="/newUser">Add New User</Link>
                </div>
                
            );

        const showSuccess = () => {

           // style = {{display: successMessage ? '': 'none'}}
        //    {successMessage}
            console.log(success)
            if (success) {
            return (
            <div className='alert alert-info' >
                <h2>Orden Creada Correctamente</h2>
            </div>    
                )
            }
        }
                
        

     return (

        <div className='row mt-5'>
        <div className='col-lg-3 col-md-6 col-sm-12'>
            <Navigation/>

        </div>
        <div className='col-lg-7 col-md-6 px-5'>

       
        <Home  title = 'Ingreso datos de Venta'/>
        {showNoUser()}
        {showError()}
        {showSuccess()}
        

        <form   onSubmit={clickSubmit}>

         <div className='pl-2 row mt-4'>
             
            <div class="form-group col-md-4">
                <label>Name <input className='form-control'  type="text"
                 name="name"
                 value= {order.name}
                 onChange={handleChange}
                 placeholder="First Name"
                 />
                 </label>
                </div>
                <div class="form-group col-md-4">
                <label>Last Name<input className='form-control'  type="text"
                 name="lastName"
                 value= {order.lastName}
                 onChange={handleChange}
                 placeholder="Last Name"
                 />
                 </label>
                </div>

            <div className='form-group col-md-4'>
                 <label>Date
                 <input className='form-control' type="date"
                 name="date"
                 value= {order.date}   
                 onChange={handleChange}
                 />
                 </label>
                 </div>

                 <div className='row mt-4'>
                 <div className='form-group col-md-4'>
                 <label>Choose a Service: </label>

                 <select  value= {order.service} onChange={handleChange} name ='service' id='cars'>
                   {/* {services.map((item,key) => (
                    <option value ="chain">{item.name}</option>
                 
                   ))} */}
                  
                     <option value ="chain">Repair Chain</option>
                    <option value ="service">Complete Service</option>
                    <option value ="brakes">Fix Brakes</option>
                    <option value ="puncture">Puncture</option>
                    <option value ="rays">Fix Rays</option>
                    <option value ="rubber">Rubber Rides</option> 
                   
                 </select>
            
                 </div>

                 <div className='form-group col-md-4'>
                 <label>Email
                 <input className='form-control' type="email"
                 name="email"
                 value= {order.email}
                 onChange={handleChange}
                 />
                 </label>
                </div>

                 <div className='form-group col-md-4'>
                 <label>City
                 <input className='form-control' type="text"
                 name="city"
                 value= {order.city}
                 onChange={handleChange}
                 />
                 </label>
                 </div>

                 <div className='row mt-4'>
                 <div className='form-group col-md-4'>
                 <label>Price
                 <input className='form-control' type="number"
                 name="price"
                 value = {order.price}
                 onChange= {handleChange}
                />
                </label>
                </div>
                <div className='form-group col-md-8'>
                <label>Note

                
                 <textarea className='form-control' rows='2' cols='250'
                 name="note"
                 value = {order.note}
                 onChange= {handleChange}
                 />
                </label>
                </div>
                 
                <div className='col-4'>
                 <button  type= "submit" className='btn btn-lg btn-success mt-5'>Success</button>
                </div>
                 
                    </div>
                 </div>
             </div>
         
            

         </form>
        
         </div>
         </div>
     )
}



export default AddPurchase;
